<div id="login" class="postbox">
  <h3 class="box-header"><span>Sign In !</span></h3>
  <div class="inside">
    <div class="login">
      <div class="login-wrap">
        <input type="text" name="username" placeholder="Tài khoản" />
        <input type="password" name="password" placeholder="Mật khẩu" />
        <input type="submit" class="button btn-auth" value="LOGIN" />
      </div>
    </div>
  </div>
</div>